import useSWR from 'swr';

// Import useSWR from swr package

// created function to handle API request
const fetcher = (...args) => fetch(...args).then((res) => res.json());

const Swr = () => {
  const {
    data: lists,
    error,
    isValidating,
  } = useSWR('https://apis.ccbp.in/list-creation/lists', fetcher);

  // Handles error and loading state
  if (error) return <div className='failed'>failed to load</div>;
  if (isValidating) return <div className="Loading">Loading...</div>;


  const groupedData = lists["lists"].reduce((acc, item) => {
    const { list_number } = item;
    if (!acc[list_number]) {
      acc[list_number] = [];
    }
    acc[list_number].push(item);
    return acc;
  }, {});

  const l = Object.entries(groupedData).map(([list_number, items]) => (
    <div key={list_number}>
      <h2>List {list_number}</h2>
      <ul>
        {items.map((item) => (
          <li key={item.id}>{item.list_number}</li>
        ))}
      </ul>
    </div>
  ));

  return (
    <div>
      {l}
    </div>
  );
};

export default Swr;
